import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import your CSS file here

const Weather = () => {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [hourlyData, setHourlyData] = useState([]);
    const [error, setError] = useState('');

    const fetchWeather = async (city) => {
        setError('');
        try {
            const response = await axios.get(`http://localhost:5000/api/weather`, {
                params: { city },
            });
            setWeatherData(response.data);
            // If city is provided, fetch hourly data separately
            if (response.data.hourly) {
                setHourlyData(response.data.hourly);
            }
        } catch (err) {
            console.error('Error fetching weather data:', err);
            setError('Error fetching weather data: ' + err.response?.data?.message || err.message);
        }
    };

    useEffect(() => {
        const getUserLocation = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    async (position) => {
                        const { latitude, longitude } = position.coords;
                        try {
                            const response = await axios.get(`http://localhost:5000/api/weather`, {
                                params: { lat: latitude, lon: longitude },
                            });
                            setWeatherData(response.data);
                            if (response.data.hourly) {
                                setHourlyData(response.data.hourly);
                            }
                        } catch (error) {
                            console.error('Error fetching weather data:', error);
                            setError('Could not retrieve weather data: ' + error.response?.data?.message || error.message);
                        }
                    },
                    (error) => {
                        console.error('Error getting location:', error);
                        setError('Could not retrieve location.');
                    }
                );
            } else {
                setError('Geolocation is not supported by this browser.');
            }
        };

        getUserLocation();
    }, []);

    const handleFetchWeather = () => {
        if (city) {
            fetchWeather(city);
        }
    };

    return (
        <div className="weather-container">
            <h1>Weather App</h1>
            <input
                type="text"
                placeholder="Enter city"
                value={city}
                onChange={(e) => setCity(e.target.value)}
            />
            <button onClick={handleFetchWeather}>Get Weather</button>

            {error && <p className="error">{error}</p>}
            
            {weatherData && (
                <div className="weather-info">
                    <h2>{weatherData.name}</h2>
                    <p>Temperature: {weatherData.main.temp} °C</p>
                    <p>Weather: {weatherData.weather[0].description}</p>
                </div>
            )}

            {hourlyData.length > 0 && (
                <div className="hourly-info">
                    <h2>Hourly Forecast</h2>
                    {hourlyData.slice(0, 24).map((hour, index) => (
                        <div key={index}>
                            <p>Hour {new Date(hour.dt * 1000).getHours()}:00</p>
                            <p>Temperature: {hour.temp} °C</p>
                            <p>Weather: {hour.weather[0].description}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Weather;
